entry.value:
import json

jython_available = True

try:
    # This is used in the NiFi ExecuteScript context
    import java.io  # noqa
    from org.apache.commons.io import IOUtils
    from java.nio.charset import StandardCharsets
    from org.apache.nifi.processor.io import StreamCallback
except ImportError:
    jython_available = False
    print "Skipping Jython imports"

def get_commands(tables, results):
    for x in tables:
        fields = get_fields(x["fields"])
        if " id " in fields:
            fields = fields.replace(" id ", " "+x["name"]+"_id ")
        pstgr_statement =  ("CREATE TABLE %s (%s); " % (x["name"], fields))
        results = results + pstgr_statement

    return results

def get_fields(arr):
    fields_list = []

    for item in arr:
        if "{" in str(item["type"]):
            # The type is an object, so default to type varchar
            field_type = "VARCHAR"
            reference_name = str(item["name"]) + "_id"
        if str(item["type"]) == "string":
            field_type = "VARCHAR"
            reference_name = str(item["name"])
        if str(item["type"]) == "int":
            field_type = "INTEGER"
            reference_name = str(item["name"])
        if str(item["type"]) == "boolean":
            field_type = "BOOLEAN"
            reference_name = str(item["name"])
        if str(item["type"]) == "null":
            field_type = "VARCHAR"
            reference_name = str(item["name"])

        fields_list.append(reference_name + " " + field_type)

    fields = ", ".join(fields_list)
    return fields

def get_table(data, object_array):
    if "namespace" in data:
        table_name = data["name"] + "_" + data["namespace"]
    else:
        table_name = data["name"]

    fld = {"name" : table_name, "type" : data["type"] , "fields" : data["fields"]}
    object_array.append(fld)

    for field in data["fields"]:
        if type(field["type"]) is dict:
            if field["type"]["type"] == "record":
                get_tables(field["type"], object_array)
            else:
                if type(field["type"]["items"])is dict:
                    get_tables(field["type"]["items"], object_array)

    return object_array

if jython_available:
    class PyStreamCallback(StreamCallback):

        def __init__(self):
            pass

        def process(self, inputStream, outputStream):
            text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
            obj = json.loads(text)
            object_array = []
            result = ""
            tables = get_table(obj, object_array)
            sql_commands = get_commands (tables, result)

            outputStream.write(bytearray(
                json.dumps(sql_commands, indent=4).encode('utf-8')))

    flowFile = session.get()

    if flowFile is not None:
        flowFile = session.write(flowFile, PyStreamCallback())

        session.transfer(flowFile, REL_SUCCESS)
