entry.value:
import json

jython_available = True

try:
    # This is used in the NiFi ExecuteScript context
    import java.io  # noqa
    from org.apache.commons.io import IOUtils
    from java.nio.charset import StandardCharsets
    from org.apache.nifi.processor.io import StreamCallback
except ImportError:
    jython_available = False
    print "Skipping Jython imports"

def get_list(obj, permissions_list):
    for x in obj:
        item = x.split('|')
    try:
        a = item[1]
        b = item[2]
    except IndexError:
        pass

    facility_program_pair = {
        "facility_id" : a,
        "program_id": b
    }

    if facility_program_pair not in permissions_list:
        permissions_list.append(facility_program_pair)

    return permissions_list

if jython_available:
    class PyStreamCallback(StreamCallback):

        def __init__(self):
            pass

        def process(self, inputStream, outputStream):
            text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
            obj = json.loads(text)
            permissions_list = []
            result = ""
            result_list = get_list(obj, permissions_list)

            outputStream.write(bytearray(
                json.dumps(result_list, indent=4).encode('utf-8')))

    flowFile = session.get()

    if flowFile is not None:
        flowFile = session.write(flowFile, PyStreamCallback())

        session.transfer(flowFile, REL_SUCCESS)
